INSERT INTO gossip_menu_option (`MenuID`, `OptionID`, `OptionIcon`, `OptionText`, `OptionBroadcastTextID`, `OptionType`, `OptionNpcFlag`, `ActionMenuID`, `ActionPoiID`, `BoxCoded`, `BoxMoney`, `BoxText`, `BoxBroadcastTextID`) VALUES ('3669', '0', '0', 'I live only to serve, Warchief! My life is empty and meaningless without your guidance.', '8318', '1', '1', '57151', NULL, '0', '0', '', NULL);
INSERT INTO gossip_menu_option (`MenuID`, `OptionID`, `OptionIcon`, `OptionText`, `OptionBroadcastTextID`, `OptionType`, `OptionNpcFlag`, `ActionMenuID`, `ActionPoiID`, `BoxCoded`, `BoxMoney`, `BoxText`, `BoxBroadcastTextID`) VALUES ('3668', '0', '0', 'I... I did not think of it that way, Warchief.', '8316', '1', '1', '3669', NULL, '0', '0', '', NULL);
INSERT INTO gossip_menu_option (`MenuID`, `OptionID`, `OptionIcon`, `OptionText`, `OptionBroadcastTextID`, `OptionType`, `OptionNpcFlag`, `ActionMenuID`, `ActionPoiID`, `BoxCoded`, `BoxMoney`, `BoxText`, `BoxBroadcastTextID`) VALUES ('3667', '0', '0', 'With all due respect, Warchief - why not allow them to be destroyed? Does this not strengthen our position?', '8314', '1', '1', '3668', NULL, '0', '0', '', NULL);
